import React, { useState } from "react";
import "../styles/Crossword.css";

function Crossword() {
    const grid = [
        [null, null, null, null, null, null, null, "L", "O", "A", "D", "E", "R"],
        [null, null, null, null, null, null, null, null, "P", null, null, null, null],
        [null, null, null, null, null, null, null, null, "A", null, null, null, null],
        [null, null, null, null, null, null, null, null, "C", null, null, null, null],
        [null, "K", null, "T", "R", "A", "N", "S", "I", "T", "I", "O", "N"],
        [null, "E", null, "I", null, null, null, null, "T", null, null, null, null],
        [null, "Y", null, "M", null, null, null, null, "Y", null, null, null, null],
        [null, "F", null, "I", null, null, null, null, null, null, null, null, null],
        ["T", "R", "A", "N", "S", "I", "T", "I", "O", "N", "E", "N","D"],
        [null, "A", null, "G", null, null, null, null, null, null, null, null, null],
        [null, "M", null, "F", null, null, null, null, null, null, null, null, null],
        [null, "E", null, "U", null, "J", null, null, null, null, null, null, null],
        [null, "S", null, "N", null, "A", null, null, null, null, null, null, null],
        [null, null, null, "C", null, "V", null, null, null, null, null, null, null],
        [null, null, null, "T", "R", "A", "N", "S", "F", "O", "R", "M", null],
        [null, null, null, "I", null, "S", null, null, null, null, null, null, null],
        [null, null, null, "O", null, "C", null, null, null, null, null, null, null],
        [null, null, null, "N", null, "R", null, null, null, null, null, null, null],
        [null, null, null, null, null, "I", "N", "F", "I", "T", "E", null, null],
        [null, null, null, null, null, "P", null, null, null, null, null, null, null],
        [null, null, null, null, null, "T", null, null, null, null, null, null, null]
    ];

    const questions = [
        { number: 1, row: 0, col: 0, direction: "Горизонтально", text: "Свойство CSS для плавного изменения значений." },
        { number: 2, row: 2, col: 0, direction: "Горизонтально", text: "Функция, задающая скорость анимации." },
        { number: 3, row: 4, col: 0, direction: "Горизонтально", text: "Событие JS, срабатывающее при завершении CSS-перехода." },
        { number: 4, row: 6, col: 0, direction: "Горизонтально", text: "Свойство, используемое для исчезновения элемента." },
        { number: 5, row: 8, col: 0, direction: "Горизонтально", text: "Пример анимированного элемента, показывающего загрузку." },

        { number: 6, row: 0, col: 0, direction: "Вертикально", text: "CSS-правило для сложных анимаций." },
        { number: 7, row: 3, col: 0, direction: "Вертикально", text: "Свойство для перемещения или вращения элемента." },
        { number: 8, row: 5, col: 0, direction: "Вертикально", text: "Значение, чтобы анимация повторялась бесконечно." },
        { number: 9, row: 7, col: 0, direction: "Вертикально", text: "Язык, которым управляют анимациями." },
        { number: 10, row: 9, col: 0, direction: "Вертикально", text: "Риск, связанный с мигающими анимациями." },
    ];

    const [userInput, setUserInput] = useState(
        grid.map(row => row.map(cell => (cell ? "" : null)))
    );

    const correctAnswers = grid.map(row => row.map(cell => (cell ? cell : null)));

    const handleChange = (e, rowIdx, colIdx) => {
        const value = e.target.value.toUpperCase();
        if (value.length > 1) return;
        const newUserInput = [...userInput];
        newUserInput[rowIdx][colIdx] = value;
        setUserInput(newUserInput);
    };

    return (
        <div className="crossword-container">
            <h1 className="crossword-title">Кроссворд</h1>

            <div className="crossword-content">
                {/* Кроссвордная сетка */}
                <div className="crossword-grid">
                    {grid.map((row, rowIdx) => (
                        <div key={rowIdx} className="crossword-row">
                            {row.map((cell, colIdx) => {
                                const questionNumber = questions.find(q => q.row === rowIdx && q.col === colIdx)?.number;

                                return (
                                    <div key={colIdx} className="crossword-cell">
                                        {questionNumber && <span className="cell-number">{questionNumber}</span>}
                                        {grid[rowIdx][colIdx] ? (
                                            <input
                                                type="text"
                                                maxLength="1"
                                                value={userInput[rowIdx][colIdx] || ""}
                                                onChange={e => handleChange(e, rowIdx, colIdx)}
                                                className={
                                                    userInput[rowIdx][colIdx] === ""
                                                        ? ""
                                                        : userInput[rowIdx][colIdx] === correctAnswers[rowIdx][colIdx]
                                                            ? "correct"
                                                            : "incorrect"
                                                }
                                            />
                                        ) : (
                                            <div className="empty-cell"></div>
                                        )}
                                    </div>
                                );
                            })}
                        </div>
                    ))}
                </div>

                {/* Вопросы справа */}
                <div className="questions">
                    <h2>Вопросы:</h2>
                    {questions.map(q => (
                        <p key={q.number}><strong>{q.number}. {q.direction}:</strong> {q.text}</p>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Crossword;
