/**
 * –û—Å–Ω–æ–≤–Ω–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞ CSS –∫–æ–º–ø–∏–ª—è—Ç–æ—Ä–∞ –∞–Ω–∏–º–∞—Ü–∏–π
 */
import { useState, useEffect } from "react";
import CodeEditor from './components/CodeEditor';
import AnimationPreview from './components/AnimationPreview';
import Notification from './components/Notification';
import Resizer from './components/Resizer';
import './style.css'

// –°—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–π –ø—Ä–∏–º–µ—Ä CSS –∞–Ω–∏–º–∞—Ü–∏–∏
const DEFAULT_CSS = `@keyframes bounce {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-20px); }
}

#previewElement {
  animation: bounce 1s infinite;
}`;

function TasksPage() {
  const [cssCode, setCssCode] = useState(DEFAULT_CSS);
  const [compiledCss, setCompiledCss] = useState(DEFAULT_CSS);
  const [autoRun, setAutoRun] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [previewSize, setPreviewSize] = useState('desktop');
  const [notification, setNotification] = useState({
    message: '',
    type: 'success',
    show: false
  });

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º —Ç—ë–º–Ω—ã–π —Ä–µ–∂–∏–º –∏–∑ localStorage
  useEffect(() => {
    const savedDarkMode = localStorage.getItem('darkMode') === 'true';
    setDarkMode(savedDarkMode);
    document.body.classList.toggle('dark', savedDarkMode);

    // –î–æ–±–∞–≤–ª—è–µ–º —Å—Ç–∏–ª–∏ –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
    const styleLink = document.createElement('link');
    styleLink.rel = 'stylesheet';
    styleLink.href = './css-compiler/style.css';
    document.head.appendChild(styleLink);

    return () => {
      // –£–¥–∞–ª—è–µ–º —Å—Ç–∏–ª–∏ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
      if (document.head.contains(styleLink)) {
        document.head.removeChild(styleLink);
      }
    };
  }, []);

  // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ —Ç–µ–º–Ω–æ–≥–æ —Ä–µ–∂–∏–º–∞
  const toggleDarkMode = () => {
    const newDarkMode = !darkMode;
    setDarkMode(newDarkMode);
    localStorage.setItem('darkMode', String(newDarkMode));
    document.body.classList.toggle('dark', newDarkMode);
  };

  // –ó–∞–ø—É—Å–∫/–ø—Ä–∏–º–µ–Ω–µ–Ω–∏–µ –∞–Ω–∏–º–∞—Ü–∏–∏
  const runAnimation = () => {
    try {
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –≤–∞–ª–∏–¥–µ–Ω –ª–∏ CSS (—É–ø—Ä–æ—â–µ–Ω–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞)
      const testStyle = document.createElement('style');
      testStyle.textContent = cssCode;
      document.head.appendChild(testStyle);
      document.head.removeChild(testStyle);

      // –ü—Ä–∏–º–µ–Ω—è–µ–º CSS
      setCompiledCss(cssCode);
      showNotification('Animation applied successfully!', 'success');
    } catch (error) {
      showNotification(`CSS Error: ${error.message || 'An unknown error occurred'}`, 'error');
    }
  };

  // –°–±—Ä–æ—Å —Ä–µ–¥–∞–∫—Ç–æ—Ä–∞
  const resetEditor = () => {
    setCssCode(DEFAULT_CSS);
    setCompiledCss(DEFAULT_CSS);
    showNotification('Editor reset to default example', 'success');
  };

  // –ö–æ–ø–∏—Ä–æ–≤–∞–Ω–∏–µ CSS –≤ –±—É—Ñ–µ—Ä –æ–±–º–µ–Ω–∞
  const copyCSS = () => {
    navigator.clipboard.writeText(cssCode).then(() => {
      showNotification('CSS copied to clipboard!', 'success');
    }).catch(() => {
      showNotification('Failed to copy to clipboard', 'error');
    });
  };

  // –ü–æ–∫–∞–∑ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è
  const showNotification = (message, type) => {
    setNotification({ message, type, show: true });
    setTimeout(() => {
      setNotification(prev => ({ ...prev, show: false }));
    }, 3000);
  };

  // –≠—Ñ—Ñ–µ–∫—Ç –¥–ª—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–≥–æ –∑–∞–ø—É—Å–∫–∞ –∞–Ω–∏–º–∞—Ü–∏–∏ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –∫–æ–¥–∞ (–µ—Å–ª–∏ –≤–∫–ª—é—á–µ–Ω autoRun)
  useEffect(() => {
    if (autoRun) {
      runAnimation();
    }
  }, [cssCode, autoRun]);

  return (
    <div className="container" style={{ paddingTop: '2rem', paddingBottom: '2rem' }}>
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <header className="header">
        <div className="header-content">
          <h1>CSS Animation Compiler</h1>
          <div className="header-actions">
            <button 
              className="button button-outline"
              onClick={toggleDarkMode}
              style={{ color: 'white' }}
            >
              {darkMode ? '‚òÄÔ∏è' : 'üåô'}
            </button>
          </div>
        </div>
      </header>

      {/* –£–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ */}
      <Notification 
        message={notification.message} 
        type={notification.type} 
        show={notification.show} 
        onClose={() => setNotification(prev => ({ ...prev, show: false }))} 
      />

      {/* –ö–∞—Ä—Ç–æ—á–∫–∞ —Å –æ–ø–∏—Å–∞–Ω–∏–µ–º */}
      <div className="card" style={{ marginTop: '1.5rem' }}>
        <div className="card-content">
          <h2 style={{ fontSize: '1.25rem', fontWeight: '500', marginBottom: '1rem' }}>
            CSS Animation Interactive Compiler
          </h2>
          <p style={{ marginBottom: '1rem', color: '#555' }}>
            Write your CSS animation code in the editor and see the results in real-time. 
            The preview element is a simple colored box that will display your animation.
          </p>
          <div className="code-example">
{`@keyframes bounce {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-20px); }
}

#previewElement {
  animation: bounce 1s infinite;
}`}
          </div>
        </div>
      </div>

      {/* –ö–æ–Ω—Ç–µ–π–Ω–µ—Ä —Ä–µ–¥–∞–∫—Ç–æ—Ä–∞ */}
      <div className="editor-container">
        <CodeEditor 
          code={cssCode} 
          onChange={setCssCode}
          darkMode={darkMode}
          onRun={runAnimation}
          onReset={resetEditor}
          onCopy={copyCSS}
          autoRun={autoRun}
          setAutoRun={setAutoRun}
        />

        <Resizer orientation="vertical" />

        <AnimationPreview 
          css={compiledCss}
          previewSize={previewSize}
          setPreviewSize={setPreviewSize}
          darkMode={darkMode}
        />
      </div>
    </div>
  );
}

export default TasksPage;
